mapper.select:
  SELECT
	t1.*
FROM
    repairinfo t1


    '1' = '1'

    and t1.hospitalname = #{hospitalname}


and t1.repairtype = #{repairtype}


    and t1.equipmentname = #{equipmentname}


    and t1.equipmenttype = #{equipmenttype}

    and str_to_date(t1.repairdate,'%Y-%m-%d') between #{beginDate} and #{endDate}

    order by t1.repairdate desc
