mapper.select:
SELECT distinct(e.inputdatetime) inputdatetime, a.equipmenttypeid, a.equipmentno, a.equipmentname, b.instrumentno, c.instrumentconfigid, c.instrumentparamconfigNO, d.instrumentconfigname, a.hospitalcode, e.warningremark FROM monitorequipment a LEFT JOIN monitorinstrument b ON a.equipmentno = b.equipmentno LEFT JOIN instrumentparamconfig c ON b.instrumentno = c.instrumentno LEFT JOIN instrumentconfig d ON c.instrumentconfigid = d.instrumentconfigid RIGHT JOIN warningrecord e ON c.instrumentparamconfigNO = e.instrumentparamconfigNO INNER JOIN ( SELECT instrumentparamconfigNO, max(inputdatetime) inputdatetime FROM warningrecord GROUP BY instrumentparamconfigNO ORDER BY inputdatetime DESC ) f ON ( c.instrumentparamconfigNO = f.instrumentparamconfigNO AND e.inputdatetime = f.inputdatetime
)
WHERE a.hospitalcode = #{hospitalcode} AND ifnull(a.clientvisible, '0') = '1' and ifnull(c.warningphone,'0') = '1' -- and e.isPhone = '1' ORDER BY inputdatetime DESC
