mapper.select:
SELECT t2.instrumenttypeid, t2.instrumenttypename, t3.instrumentconfigid, t3.instrumentconfigname, t1.lowlimit, t1.saturation, t1.highlimit FROM instrumentmonitor t1 LEFT JOIN monitorinstrumenttype t2 ON t1.instrumenttypeid = t2.instrumenttypeid LEFT JOIN instrumentconfig t3 on t1.instrumentconfigid =t3.instrumentconfigid where 1=1
and t1.instrumenttypeid= #{instrumenttypeid}
ORDER BY t1.instrumenttypeid
