mapper.select:
SELECT g.hospitalname, e.equipmenttypename, e.equipmenttypename_us, d.equipmentname, b.sn, a.instrumentno , a.instrumentconfigid, a.instrumenttypeid, c.instrumenttypename, a.instrumentname, a.firsttime, a.channel, a.instrumentconfigid, f.instrumentconfigname, a.alarmtime, a.lowlimit, a.highlimit, a.saturation, a.warningphone, b.hospitalcode, a.instrumentno, a.calibration, a.instrumentparamconfigNO as instrumentparamconfigno, a.unit FROM instrumentparamconfig a LEFT JOIN monitorinstrument b ON a.instrumentno = b.instrumentno LEFT JOIN monitorinstrumenttype c ON c.instrumenttypeid = a.instrumenttypeid LEFT JOIN monitorequipment d ON d.equipmentno = b.equipmentno LEFT JOIN monitorequipmenttype e ON e.equipmenttypeid = d.equipmenttypeid LEFT JOIN instrumentconfig f ON f.instrumentconfigid = a.instrumentconfigid LEFT JOIN hospitalofreginfo g ON g.hospitalcode = d.hospitalcode where 1=1
and g.hospitalcode = #{hospitalCode}
and e.equipmenttypeid = #{equipmentTypeId}
and b.instrumentno = #{instrumentNo}
and b.sn like concat('%',#{sn},'%')
order by instrumentconfigid
