mapper.select:
SELECT equipmentno, ${param.field}, inputdatetime from lab_mon.monitorequipmentlastdata WHERE toYYYYMM ( inputdatetime ) IN ( #{param.yearMonth} ) and formatDateTime(inputdatetime,'%Y-%m-%d %H:%M:%S') BETWEEN #{param.startTime} AND #{param.endTime} AND equipmentno = #{param.equipmentNo} AND ${param.field} != ''
and ${res.field} ${res.condition} #{res.value}
ORDER BY inputdatetime

SELECT
  equipmentno,
  ${param.field},
  inputdatetime
FROM
    lab_mon.monitorequipmentlastdata
WHERE toYYYYMM ( inputdatetime ) IN ( #{param.yearMonth} )
  AND formatDateTime ( inputdatetime, '%H:%M' ) BETWEEN #{param.minTime} AND #{param.maxTime}
  AND equipmentno = #{param.equipmentNo}


        and ${res.field} ${res.condition} #{res.value}

select equipmentno,
${field},
inputdatetime from lab_mon.monitorequipmentlastdata where toYYYYMM ( inputdatetime ) IN ( #{param.yearMonth} ) AND inputdatetime >= #{param.startTime} AND inputdatetime <= #{param.endTime} AND equipmentno = #{param.equipmentNo}

SELECT
inputdatetime,
${instrumentConfigName}
FROM
lab_mon.monitorequipmentlastdata
WHERE
toYYYYMM ( inputdatetime ) IN (#{ym})
and equipmentno  = #{equipmentNo}
AND  inputdatetime >= #{startTime}
AND  inputdatetime <=  #{endTime}
ORDER BY inputdatetime ASC

SELECT
    *
FROM
    lab_mon.monitorequipmentlastdata
WHERE
    toYYYYMM ( inputdatetime ) IN (#{ym})
  AND  inputdatetime >= #{startTime}
  AND  inputdatetime <=  #{endTime}
  AND equipmentno = #{equipmentNo}
  AND formatDateTime ( inputdatetime, '%Y-%m' ) = #{month}

select formatDateTime(inputdatetime,'%Y-%m-%d')  as remark1,count(1) remark2
from lab_mon.monitorequipmentlastdata
WHERE hospitalcode = #{param.hospitalCode} and cmdid ='88' and toYYYYMM ( inputdatetime ) IN ( #{param.yearMonth} )  and equipmentno = #{param.equipmentNo}
group by remark1 order by remark1;
