mapper.select:
        SELECT
        toStartOfDay(inputdatetime) time,
	    COUNT(*) num
        FROM lab_mon.warningrecord
        WHERE id IN ( #{param.yearMonth} )
          and equipmentno = #{param.equipmentNo}
          and inputdatetime >= #{param.startTime}
          and inputdatetime <= #{param.endTime}
        group by toStartOfDay(inputdatetime)
        ORDER BY time

SELECT
    *
FROM
    lab_mon.warningrecord
WHERE
    equipmentno = #{equipmentNo}
    and inputdatetime >= #{startTime}
    and inputdatetime <= #{endTime}
order by inputdatetime desc

select
    *
from lab_mon.warningrecord
where hospitalcode = #{hospitalCode}
  and inputdatetime >= #{startTime}
  and inputdatetime <= #{endTime}

SELECT
*
FROM
lab_mon.warningrecord
WHERE
hospitalcode  = #{param.hospitalCode}
AND  inputdatetime >= #{param.startTime}
AND  inputdatetime <= #{param.endTime}

    AND  equipmentno  in (${param.equipmentNo})

ORDER by inputdatetime

SELECT equipmentno, COUNT(equipmentno) num FROM lab_mon.warningrecord WHERE inputdatetime >= #{param.startTime} AND inputdatetime <= #{param.endTime} AND hospitalcode = #{param.hospitalCode}
and equipmentno in (${param.equipmentNo})
GROUP BY equipmentno ORDER by num DESC
