mapper.select:
SELECT username FROM userback WHERE userid = #{userId}

UNION

SELECT username FROM userright WHERE userid = #{userId}
limit 1

SELECT u.userid userid ,u.username username ,u.pwd pwd ,u.nickname nickname ,h.hospitalname hospitalName ,h.hospitalcode hospitalCode ,u.phonenum phoneNum ,u.isuse isUse ,u.usertype userType ,u.devicetype deviceType ,u.timeout timeout ,u.timeoutwarning timeoutWarning ,u.reminders reminders ,u.mailbox mailbox ,u.role role ,u.national_id nationalId FROM userright u LEFT JOIN hospitalofreginfo h ON u.hospitalcode = h.hospitalcode where 1=1
and h.hospitalcode = #{hospitalCode}
and u.isuse = #{isUse}
and (u.nickname like concat('%',#{username},'%') or u.phonenum like concat('%',#{username},'%'))
